#include <stdlib.h>  // For abs()

void draw_character(Slugfox *slugfox) {
    int x = slugfox->chara_pos.x;
    int y = slugfox->chara_pos.y;
    pixel_rgb_t (*sprite)[slugfox->h][slugfox->w];

    int frame_duration = 5;
    int anim_frame = (frame_counter / frame_duration) % 4;
    if (slugfox->face_dir == 1) {  // Facing right
        switch (anim_frame) {
            case 0:
                sprite = &slugfox->static_right_1;
                break;
            case 1:
                sprite = &slugfox->static_right_2;
                break;
            case 2:
                sprite = &slugfox->static_right_3;
                break;
            case 3:
                sprite = &slugfox->static_right_1;
                break;
            default:
                sprite = &slugfox->static_right_1;
        }
    } else {  // Facing left
        switch (anim_frame) {
            case 0:
                sprite = &slugfox->static_left_1;
                break;
            case 1:
                sprite = &slugfox->static_left_2;
                break;
            case 2:
                sprite = &slugfox->static_left_3;
                break;
            case 3:
                sprite = &slugfox->static_left_1;
                break;
            default:
                sprite = &slugfox->static_left_1;
        }
    }

    // Tolerance for detecting near-white pixels
    const int tolerance = 2;

    // Draw the sprite, skipping pixels that are near white or pure black
    for (int dy = 0; dy < slugfox->h; dy++) {
        for (int dx = 0; dx < slugfox->w; dx++) {
            int px = x + dx;
            int py = y + dy;
            pixel_rgb_t pixel = (*sprite)[dy][dx];
            // Skip pixels that are near white (within tolerance of WHITE) or pure black
            if ((abs(pixel.r - WHITE.r) <= tolerance &&
                 abs(pixel.g - WHITE.g) <= tolerance &&
                 abs(pixel.b - WHITE.b) <= tolerance)) {
                continue;
            }
            vp->bfbp->pixels[py][px] = pixel;
        }
    }
}


void draw_trigger(Position *trigg_pos) {
    // 5x5 red square for trigger (only for levels 1-9)
    if (current_level > 0 && current_level <= 10) {
        int tx = trigg_pos->x;
        int ty = trigg_pos->y;
        for (int dy = 0; dy < 5; dy++) {
            for (int dx = 0; dx < 5; dx++) {
                int px = tx + dx;
                int py = ty + dy;
                if (px >= 0 && px < WIDTH && py >= 0 && py < HEIGHT) {
                    vp->bfbp->pixels[py][px] = RED;
                }
            }
        }
    }
}

void trigger_collision(Position *chara_pos){
    if (current_level <= 9) {
        int tx = trigger_pos[current_level].x;
        int ty = trigger_pos[current_level].y;
        if (chara_pos->x + slugfox.w >= tx && chara_pos->x <= tx + slugfox.w &&
            chara_pos->y + slugfox.h >= ty && chara_pos->y <= ty + slugfox.h) {
            current_level++;
            if (current_level > 10) current_level = 10;
            //将玩家位置重设为关卡开始点
            *chara_pos = init_pos[current_level];
            draw_init_level(current_level);  // Redraw new level
            write_hex(current_level);   
        }
    }
}

static int horizontal_velocity = 0;


void update_character_movement(Position *chara_pos) {
    int switches = read_switches(); // Read switch status (if used)
    int jumpPressed = 0;
    int manualDownPressed = 0;
    int leftPressed = 0;
    int rightPressed = 0;
    int PS2_data, RVALID;
    unsigned char key;

    // Process all keys in the FIFO and set flags for each relevant key
    while (1) {
        PS2_data = *(PS2_ptr);             // Read data from the PS/2 port
        RVALID = (PS2_data & 0x8000);        // Check if data is valid (high bit indicates validity)
        if (!RVALID) {
            break; // Exit if FIFO is empty
        }
        key = PS2_data & 0xFF;               // Get the scan code (lower 8 bits)
        if (key == 0xF0) {                   // Ignore break codes
            continue;
        }
        switch (key) {
            case 0x1D:  // W key: jump trigger
                jumpPressed = 1;
                break;
            case 0x1B:  // S key: manual downward movement
                manualDownPressed = 1;
                break;
            case 0x1C:  // A key: left movement
                leftPressed = 1;
                break;
            case 0x23:  // D key: right movement
                rightPressed = 1;
                break;
            default:
                break;
        }
    }

    // On the ground, allow normal horizontal movement from input
    if (!is_jumping) {
        if (leftPressed) {
            if (chara_pos->x > 0)
                chara_pos->x -= 3;
            slugfox.face_dir = 0; // Face left
        }
        if (rightPressed) {
            if (chara_pos->x + slugfox.w < WIDTH)
                chara_pos->x += 3;
            slugfox.face_dir = 1; // Face right
        }
    }

    // If jump key is pressed and character is on the ground and not already jumping, initiate jump
    if (jumpPressed && !is_jumping && (chara_pos->y + slugfox.h >= GROUND_LEVEL)) {
        vertical_velocity = jump_initial_velocity;
        is_jumping = 1;
        // Capture horizontal direction at jump initiation; in air, horizontal movement will not change
        if (leftPressed && !rightPressed) {
            horizontal_velocity = -3;
            slugfox.face_dir = 0; // Face left
        } else if (rightPressed && !leftPressed) {
            horizontal_velocity = 3;
            slugfox.face_dir = 1; // Face right
        } else {
            horizontal_velocity = 0; // Pure vertical jump
        }
    }

    // If manual downward movement (S key) is pressed and not jumping, move down
    if (manualDownPressed && !is_jumping && (chara_pos->y + slugfox.h < HEIGHT)) {
        chara_pos->y += 3;
    }

    // If character is not on the ground, ensure falling (even if not initiated by jump)
    if (!is_jumping && (chara_pos->y + slugfox.h < GROUND_LEVEL)) {
        is_jumping = 1;
        vertical_velocity = 0;  // Start falling with zero vertical speed
    }

    // If the character is in the air, apply gravity and fixed horizontal velocity (captured at jump start)
    if (is_jumping) {
        // Update horizontal position with the fixed horizontal_velocity
        chara_pos->x += horizontal_velocity;
        // Update vertical position and velocity
        chara_pos->y += vertical_velocity;
        vertical_velocity += gravity;
        // When reaching or passing the ground, land the character
        if (chara_pos->y + slugfox.h >= GROUND_LEVEL) {
            chara_pos->y = GROUND_LEVEL - slugfox.h;
            vertical_velocity = 0;
            horizontal_velocity = 0;
            is_jumping = 0;
        }
    }
}



//==============================
//====== 火车从这里开始 =========
//==============================

void train_gen(){
    if (current_level > 0) {  // Only generate for levels 1-10
        for (int i = 0; i < 5; i++) {
            trains[i] = level_trains[current_level].trains[i];
        }
    }
}

int train_collision(const Position *chara_pos) {
    int chara_left = chara_pos->x;
    int chara_right = chara_pos->x + slugfox.w;
    int chara_top = chara_pos->y;
    int chara_bottom = chara_pos->y + slugfox.h;

    for (int i = 0; i < 5; i++) {
        if (trains[i].active) {
            int train_left = trains[i].pos.x;
            int train_right = trains[i].pos.x + trains[i].w;
            int train_top = trains[i].pos.y;
            int train_bottom = trains[i].pos.y + trains[i].h;

            if (chara_right >= train_left && chara_left <= train_right &&
                chara_bottom >= train_top && chara_top <= train_bottom) {
                return 1;  // Collision detected
            }
        }
    }
    return 0;  // No collision
}

void update_train(Train *train) {
    if (train->active) {
        train->pos.x += train->hor_speed;
        train->pos.y += train->ver_speed;
        if (train->pos.x <= 0) {
            train->pos.x = 0;
            train->hor_speed = -train->hor_speed;
        }
        else if (train->pos.x + train->w >= WIDTH) {
            train->pos.x = WIDTH - train->w;
            train->hor_speed = -train->hor_speed;
        }
        if (train->pos.y <= 0) {
            train->pos.y = 0;
            train->ver_speed = -train->ver_speed;
        }
        else if (train->pos.y + train->h >= HEIGHT) {
            train->pos.y = HEIGHT - train->h;
            train->ver_speed = -train->ver_speed;
        }
    }
}
void draw_train(const Train *train) {
    if (train->active) {
        int x = train->pos.x;
        int y = train->pos.y;
        for (int dy = 0; dy < train->h; dy++) {
            for (int dx = 0; dx < train->w; dx++) {
                int px = x + dx;
                int py = y + dy;
                if (px >= 0 && px < WIDTH && py >= 0 && py < HEIGHT) {
                    vp->bfbp->pixels[py][px] = BLUE;
                }
            }
        }
    }
}

//==============================
//====== 全部功能大总和 =========
//==============================

void swap_buffers() {
    vp->fbp = 1;  // Request swap
    while ((vp->StatusControl & 1) != 0);  // Wait for swap to complete
}

void update_frame_counter() {
    frame_counter++;
}

//无论如何都要保持每帧刷新屏幕，这是因为后面会加入动画
void redraw_frame(const Position *chara_pos) {
    clear_screen();  // Clear once per frame
    // Draw background
    for (int y = 50; y < 200; y++) {
        for (int x = 20 + (current_level * 10); x < 300 - (current_level * 10); x++) {
            if (x >= 0 && x < WIDTH) {
                vp->bfbp->pixels[y][x] = GREEN;
            }
        }
    }
    for (int i = 0; i < 5; i++) {
        if (trains[i].active) draw_train(&trains[i]);
    }
    // Draw all elements in order
    if (current_level > 0) {
        draw_trigger(&trigger_pos[current_level]);
    }

    update_frame_counter();  // Must be called here
    draw_character(&slugfox);
    swap_buffers();  // Swap only once per frame
}


	